<?php require_once '../../db_connect.php';$conn = new DatabaseConnect('localhost','root', 'root', 'senior_project');/*SQL to build table for clerk's view of the order's that have been input in to the database*/$grabSQL=$conn->query("SELECT `Ted's Butchery_clientcustomerorder`.customer_name,`Ted's Butchery_clientcustomerorder`.order_id,`Ted's Butchery_clientcustomerorderline`.product_id,`Ted's Butchery_clientcustomerorderline`.product_brand,`Ted's Butchery_clientcustomerorderline`.product_name,`Ted's Butchery_clientcustomerorderline`.product_description,`Ted's Butchery_clientcustomerorderline`.product_price,`Ted's Butchery_clientcustomerorderline`.quantity,`Ted's Butchery_clientcustomerorderline`.filled FROM `Ted's Butchery_clientcustomerorder` JOIN `Ted's Butchery_clientcustomerorderline` ON `Ted's Butchery_clientcustomerorder`.order_id =`Ted's Butchery_clientcustomerorderline`.order_id  WHERE `Ted's Butchery_clientcustomerorderline`.filled = 0 AND `Ted's Butchery_clientcustomerorderline`.company_id =1"); echo 'ORDERS<br><table>'; echo '<tr><td>NAME</td><td>ORDER ID</td><td>PRODUCT ID</td><td>PRODUCT BRAND</td><td>PRODUCT DESCRIPTION</td><td>PRODUCT PRICE</td><td>PRODUCT AMOUNT</td><td>PRODUCT QUANTITY</td></tr>'; while ($row = mysqli_fetch_array($grabSQL)) { echo '<tr>';  echo '<td>' . $row['customer_name'] . '</td>'; echo '<td id=order_id>' . $row['order_id'].  '</td>'; echo '<td id=product_id>' . $row['product_id'] . '</td>'; echo '<td>' . $row['product_brand'] . '</td>'; echo '<td>' . $row['product_name'] . '</td>'; echo '<td>' . $row['product_description'] . '</td>'; echo '<td>' . $row['product_price'] . '</td>'; echo '<td>' . $row['quantity'] . '</td>'; echo '<td style=border: none; background-color=blue;><a href=# class=remove_order>Remove</a></td>';  echo '</tr>';  echo '</div>';  }  echo '</table>'; ?>